import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  List,
  ListItem,
  ListItemText, // Make sure this is included
  Select,
  MenuItem,
  InputLabel,
  FormControl,
} from "@mui/material";

const ModifyFlights = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [flightData, setFlightData] = useState({
    departureLocation: "",
    arrivalLocation: "",
    departureDateTime: "",
    arrivalDateTime: "",
    aircraft: "",
  });

  // Placeholder data for the dropdowns
  const locations = ["New York", "London", "Tokyo", "Paris", "Sydney"];
  const aircrafts = ["Boeing 777", "Airbus A380", "Boeing 737", "Airbus A320"];

  const handleSearch = () => {
    // API call to search flight by searchTerm
    // Mock response for demonstration
    setFlightData({
      departureLocation: locations[0],
      arrivalLocation: locations[1],
      departureDateTime: "2023-01-01T10:00",
      arrivalDateTime: "2023-01-01T14:00",
      aircraft: aircrafts[0],
    });
  };

  const handleChange = (prop) => (event) => {
    setFlightData({ ...flightData, [prop]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // API call to update the flight data
    console.log("Updated Flight Data:", flightData);
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 4 }}>
        Modify Flights
      </Typography>

      {/* Search Component */}
      <Box sx={{ display: "flex", alignItems: "center", mb: 4 }}>
        <TextField
          label="Flight Number"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ mr: 2 }}
        />
        <Button variant="contained" onClick={handleSearch}>
          Search
        </Button>
      </Box>

      {/* Display and Modify Flight Information */}
      <form onSubmit={handleSubmit}>
        <List>
          {Object.entries(flightData).map(([key, value]) => (
            <ListItem key={key}>
              <ListItemText
                primary={
                  key.charAt(0).toUpperCase() +
                  key.slice(1).replace(/([A-Z])/g, " $1")
                }
              />
              {key.includes("Location") ? (
                <FormControl sx={{ ml: 2, flex: 1 }}>
                  <InputLabel>{key}</InputLabel>
                  <Select value={value} onChange={handleChange(key)}>
                    {locations.map((location, index) => (
                      <MenuItem key={index} value={location}>
                        {location}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              ) : key === "aircraft" ? (
                <FormControl sx={{ ml: 2, flex: 1 }}>
                  <InputLabel>{key}</InputLabel>
                  <Select value={value} onChange={handleChange(key)}>
                    {aircrafts.map((aircraft, index) => (
                      <MenuItem key={index} value={aircraft}>
                        {aircraft}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              ) : (
                <TextField
                  variant="outlined"
                  value={value}
                  onChange={handleChange(key)}
                  sx={{ ml: 2, flex: 1 }}
                />
              )}
            </ListItem>
          ))}
        </List>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ mt: 2 }}
        >
          Update Flight
        </Button>
      </form>
    </Container>
  );
};

export default ModifyFlights;
