import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Checkbox,
  FormControlLabel,
  Box,
  MenuItem,
  IconButton,
  Paper,
} from "@mui/material";
import Header from "../components/Header";
import Footer from "../components/Footer";
import SwapHorizIcon from "@mui/icons-material/SwapHoriz";
import { styled } from "@mui/material/styles";

const HomePage = () => {
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [departureDate, setDepartureDate] = useState("");
  const [returnDate, setReturnDate] = useState("");
  const [isRoundTrip, setIsRoundTrip] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Implement the submit logic
  };

  const handleSwap = () => {
    // Swap the values of origin and destination
    setOrigin(destination);
    setDestination(origin);
  };

  // Enhanced styles
  const StyledContainer = styled(Container)(({ theme }) => ({
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(4),
    borderRadius: theme.shape.borderRadius,
    boxShadow: theme.shadows[3],
  }));

  const StyledPaper = styled(Paper)(({ theme }) => ({
    padding: theme.spacing(3),
    margin: theme.spacing(3, 0),
    backgroundColor: "#f5f5f5", // Light grey background
  }));

  return (
    <Box>
      <Header />

      <StyledContainer maxWidth="md">
        <Typography variant="h4" gutterBottom>
          Welcome to Our Flight Reservation Service
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Find and book flights with ease.
        </Typography>

        <StyledPaper>
          <Box component="form" noValidate onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              {/* Origin TextField */}
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="origin"
                  name="origin"
                  label="Origin"
                  fullWidth
                  autoComplete="given-name"
                  variant="outlined"
                  value={origin}
                  onChange={(e) => setOrigin(e.target.value)}
                />
              </Grid>

              {/* Destination TextField */}
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="destination"
                  name="destination"
                  label="Destination"
                  fullWidth
                  autoComplete="family-name"
                  variant="outlined"
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                />
              </Grid>

              {/* Swap Icon */}
              <Grid item xs={12} sm={6}>
                <IconButton onClick={handleSwap} aria-label="swap">
                  <SwapHorizIcon />
                </IconButton>
              </Grid>

              {/* Other form elements like Date pickers and Checkboxes */}
              {/* ... [Remaining form fields and components] */}
            </Grid>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              sx={{ mt: 3, mb: 2 }}
            >
              Search Flights
            </Button>
          </Box>
        </StyledPaper>
      </StyledContainer>

      <Footer />
    </Box>
  );
};

export default HomePage;
