import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  List,
  ListItem,
  ListItemText,
  Select,
  MenuItem,
} from "@mui/material";

const AdminManageCrew = () => {
  const navigate = useNavigate();
  const [flightNumber, setFlightNumber] = useState("");
  const [crewData, setCrewData] = useState({
    flightNumber: "",
    crewMembers: [],
  });
  const [loading, setLoading] = useState(false);
  const [availableCrews, setAvailableCrews] = useState([]);
  const [selectedCrew, setSelectedCrew] = useState("");

  // Placeholder for fetching crew data
  const fetchCrewData = async (flightNum) => {
    setLoading(true);
    // Placeholder: Replace with actual API call
    console.log(`Fetching crew for flight number: ${flightNum}`);
    // Simulate fetching data
    setTimeout(() => {
      // Example data
      setCrewData({
        flightNumber: flightNum,
        crewMembers: [
          "Captain Smith",
          "First Officer Jones",
          "Flight Attendant Brown",
        ],
      });
      setLoading(false);
    }, 1000);
  };

  // Placeholder for fetching available crews
  const fetchAvailableCrews = () => {
    // Simulate fetching data
    setAvailableCrews([
      "Captain Green",
      "First Officer White",
      "Flight Attendant Black",
    ]);
  };

  const handleSearch = () => {
    fetchCrewData(flightNumber);
    fetchAvailableCrews();
  };

  const handleAddCrew = () => {
    if (selectedCrew) {
      console.log(`Add crew member: ${selectedCrew}`);
      setCrewData((prevCrewData) => ({
        ...prevCrewData,
        crewMembers: [...prevCrewData.crewMembers, selectedCrew],
      }));
      setSelectedCrew(""); // Reset the selected crew member
    }
  };

  const handleRemoveCrew = (memberName) => {
    console.log(`Remove crew member: ${memberName}`);
    setCrewData((prevCrewData) => ({
      ...prevCrewData,
      crewMembers: prevCrewData.crewMembers.filter(
        (member) => member !== memberName
      ),
    }));
  };

  const goBackToDashboard = () => {
    navigate("/admin-dashboard");
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 4 }}>
        Manage Crew
      </Typography>
      <Box sx={{ mb: 4 }}>
        <TextField
          label="Flight Number"
          value={flightNumber}
          onChange={(e) => setFlightNumber(e.target.value)}
          sx={{ mr: 2 }}
        />
        <Button variant="contained" onClick={handleSearch} disabled={loading}>
          {loading ? "Loading..." : "Search"}
        </Button>
      </Box>
      <Box>
        <Select
          value={selectedCrew}
          onChange={(e) => setSelectedCrew(e.target.value)}
          displayEmpty
          fullWidth
        >
          <MenuItem value="" disabled>
            Select Crew Member
          </MenuItem>
          {availableCrews.map((crew, index) => (
            <MenuItem key={index} value={crew}>
              {crew}
            </MenuItem>
          ))}
        </Select>
        <Button variant="contained" onClick={handleAddCrew} sx={{ mt: 2 }}>
          Add Crew Member
        </Button>
      </Box>
      {crewData && (
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6">
            Crew for Flight {crewData.flightNumber}
          </Typography>
          <List>
            {crewData.crewMembers.map((member, index) => (
              <ListItem key={index}>
                <ListItemText primary={member} />
                <Button
                  color="secondary"
                  onClick={() => handleRemoveCrew(member)}
                >
                  Remove
                </Button>
              </ListItem>
            ))}
          </List>
        </Box>
      )}
      <Button
        variant="contained"
        color="primary"
        onClick={goBackToDashboard}
        sx={{ mt: 3 }}
      >
        Back to Dashboard
      </Button>
    </Container>
  );
};

export default AdminManageCrew;
