import React, { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const HomePage = () => {
  // State hooks for form inputs
  const [fromLocation, setFromLocation] = useState("");
  const [destination, setDestination] = useState("");
  const [roundTrip, setRoundTrip] = useState(false);
  const [departureDate, setDepartureDate] = useState("");
  const [arrivalDate, setArrivalDate] = useState("");
  const [numberOfTickets, setNumberOfTickets] = useState(1);

  // Example options for dropdowns (you'll replace these with actual data)
  const locations = ["New York", "Los Angeles", "Chicago", "Houston", "Miami"];
  const ticketNumbers = [1, 2, 3, 4, 5];

  return (
    <div>
      <Header />

      <main>
        <h1>Welcome to Our Flight Reservation Service</h1>
        <p>Find and book flights with ease.</p>

        {/* Flight Search Form */}
        <form>
          {/* From Location Dropdown */}
          <label>
            From Location:
            <select
              value={fromLocation}
              onChange={(e) => setFromLocation(e.target.value)}
            >
              {locations.map((location) => (
                <option key={location} value={location}>
                  {location}
                </option>
              ))}
            </select>
          </label>

          {/* Destination Dropdown */}
          <label>
            Choose Destination:
            <select
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
            >
              {locations.map((location) => (
                <option key={location} value={location}>
                  {location}
                </option>
              ))}
            </select>
          </label>

          {/* Roundtrip Checkbox */}
          <label>
            Roundtrip:
            <input
              type="checkbox"
              checked={roundTrip}
              onChange={(e) => setRoundTrip(e.target.checked)}
            />
          </label>

          {/* Departure Date */}
          <label>
            Departure Date:
            <input
              type="date"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
            />
          </label>

          {/* Arrival Date */}
          <label>
            Arrival Date:
            <input
              type="date"
              value={arrivalDate}
              onChange={(e) => setArrivalDate(e.target.value)}
            />
          </label>

          {/* Number of Tickets Dropdown */}
          <label>
            Number of Tickets:
            <select
              value={numberOfTickets}
              onChange={(e) => setNumberOfTickets(e.target.value)}
            >
              {ticketNumbers.map((number) => (
                <option key={number} value={number}>
                  {number}
                </option>
              ))}
            </select>
          </label>

          {/* Search Button */}
          <button type="submit">Search Flights</button>
        </form>
      </main>

      <Footer />
    </div>
  );
};

export default HomePage;
