import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";

const AdminManageCrew = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [crewData, setCrewData] = useState(null);
  const [loading, setLoading] = useState(false);

  // Placeholder for fetching crew data
  const fetchCrewData = async (flightNum) => {
    setLoading(true);
    // Placeholder: Replace with actual API call
    console.log(`Fetching crew for flight number: ${flightNum}`);
    // Simulate fetching data
    setTimeout(() => {
      // Example data
      setCrewData({
        flightNumber: flightNum,
        crewMembers: [
          "Captain Smith",
          "First Officer Jones",
          "Flight Attendant Brown",
        ],
      });
      setLoading(false);
    }, 1000);
  };

  const handleSearch = () => {
    fetchCrewData(flightNumber);
  };

  const handleAddCrew = () => {
    console.log("Add crew functionality");
    // Implement add crew functionality here
  };

  const handleRemoveCrew = (memberName) => {
    console.log(`Remove crew member: ${memberName}`);
    // Implement remove crew functionality here
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 4 }}>
        Manage Crew
      </Typography>
      <Box sx={{ mb: 4 }}>
        <TextField
          label="Flight Number"
          value={flightNumber}
          onChange={(e) => setFlightNumber(e.target.value)}
          sx={{ mr: 2 }}
        />
        <Button variant="contained" onClick={handleSearch} disabled={loading}>
          {loading ? "Loading..." : "Search"}
        </Button>
      </Box>
      {crewData && (
        <Box>
          <Typography variant="h6">
            Crew for Flight {crewData.flightNumber}
          </Typography>
          <List>
            {crewData.crewMembers.map((member, index) => (
              <ListItem key={index}>
                <ListItemText primary={member} />
                <Button
                  color="secondary"
                  onClick={() => handleRemoveCrew(member)}
                >
                  Remove
                </Button>
              </ListItem>
            ))}
          </List>
          {crewData.crewMembers.length === 0 && (
            <Button color="primary" onClick={handleAddCrew}>
              Add Crew
            </Button>
          )}
        </Box>
      )}
    </Container>
  );
};

export default AdminManageCrew;
