import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";

const ModifyFlights = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [flightData, setFlightData] = useState({
    departureLocation: "",
    arrivalLocation: "",
    departureDateTime: "",
    arrivalDateTime: "",
    aircraft: "",
  });

  const handleSearch = () => {
    // API call to search flight by searchTerm
    // Mock response for demonstration
    setFlightData({
      departureLocation: "New York",
      arrivalLocation: "London",
      departureDateTime: "2023-01-01T10:00",
      arrivalDateTime: "2023-01-01T14:00",
      aircraft: "Boeing 777",
    });
  };

  const handleChange = (prop) => (event) => {
    setFlightData({ ...flightData, [prop]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // API call to update the flight data
    console.log("Updated Flight Data:", flightData);
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 4 }}>
        Modify Flights
      </Typography>

      {/* Search Component */}
      <Box sx={{ display: "flex", alignItems: "center", mb: 4 }}>
        <TextField
          label="Flight Number"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ mr: 2 }}
        />
        <Button variant="contained" onClick={handleSearch}>
          Search
        </Button>
      </Box>

      {/* Display and Modify Flight Information */}
      <form onSubmit={handleSubmit}>
        <List>
          {Object.keys(flightData).map((key) => (
            <ListItem key={key}>
              <ListItemText
                primary={
                  key.charAt(0).toUpperCase() +
                  key.slice(1).replace(/([A-Z])/g, " $1")
                }
              />
              <TextField
                variant="outlined"
                value={flightData[key]}
                onChange={handleChange(key)}
                sx={{ ml: 2, flex: 1 }}
              />
            </ListItem>
          ))}
        </List>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ mt: 2 }}
        >
          Update Flight
        </Button>
      </form>
    </Container>
  );
};

export default ModifyFlights;
