import React, { useState } from "react";
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Checkbox,
  FormControlLabel,
  Box,
} from "@mui/material";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import Header from "../components/Header";
import Footer from "../components/Footer";

const HomePage = () => {
  // State hooks for form inputs
  const [fromLocation, setFromLocation] = useState("");
  const [destination, setDestination] = useState("");
  const [roundTrip, setRoundTrip] = useState(false);
  const [departureDate, setDepartureDate] = useState("");
  const [arrivalDate, setArrivalDate] = useState("");
  const [numberOfTickets, setNumberOfTickets] = useState(1);

  const navigate = useNavigate(); // Hook for navigation

  // Example options for dropdowns (replace with actual data)
  const locations = ["New York", "Los Angeles", "Chicago", "Houston", "Miami"];
  const ticketNumbers = [1, 2, 3, 4, 5];

  const handleSubmit = (event) => {
    event.preventDefault();
    if (
      !fromLocation ||
      !destination ||
      !departureDate ||
      !arrivalDate ||
      !numberOfTickets
    ) {
      alert("Please fill in all required fields.");
    } else {
      navigate("/flight-search"); // Redirect to FlightSearch page
    }
  };

  return (
    <Box>
      <Header />

      <Container maxWidth="md" sx={{ my: 4 }}>
        <Typography variant="h4" gutterBottom>
          Welcome to Our Flight Reservation Service
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Find and book flights with ease.
        </Typography>

        {/* Flight Search Form */}
        <Box component="form" noValidate sx={{ mt: 3 }} onSubmit={handleSubmit}>
          {/* ... existing Grid and TextField components ... */}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ mt: 3, mb: 2 }}
          >
            Search Flights
          </Button>
        </Box>
      </Container>

      <Footer />
    </Box>
  );
};

export default HomePage;
